def getUrl(jahr, woche):
  return "http://www.spiegel.de/spiegel/print/index-" + str(jahr) + "-"+ str(woche) +".html"

def urlToSoup(url):
  r = urlreq.urlopen(url)
  h = r.read() #get the html
  return bs(h, "html5lib") #make soup

def getOutline(url):
  s = urlToSoup(url)
  return s.find("div", {"class": "epub-content"}).findAll("a")

def getArticle(soupOfLinks, index):
  return urlToSoup("http://www.spiegel.de/spiegel/print/" + soupOfLinks[index].get("href")).find("div", {"class": "dig-artikel"}).text

def getArticleText(year, week, articleNumber):
  return getArticle(getOutline(getUrl(year,week)),articleNumber)
  
  
